			department_id
			, department_name

			, manager_id


			, location_id

		)
	   VALUES
	   (
	   		#{departmentId}
	   		, #{departmentName}

	   		, #{managerId}


	   		, #{locationId}

	   )

mapper.update:
UPDATE departments
SET
	department_name = #{dpetInfo.departmentName},
