mapper.insert:

			SELECT NVL(MAX(bno), 100) + 1
			FROM board


		INSERT INTO board
			(
				 bno
				 , title
				 , contents
				 , writer
				 , regdate
				 , image                   
             )
        VALUES 
        	(
	        	#{bno}
	        	, #{title}
	        	, #{contents}
	        	, #{regdate}
	        	, #{image}
        	)
